    private void bListarCursosFeitosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bListarCursosFeitosActionPerformed
        // TODO add your handling code here:
        EstudanteController ec = new EstudanteController();
        ArrayList<String[]> finalizados = ec.getCursosFinalizados(cpf);
        getTabelaCursosFinalizados().getDataVector().removeAllElements();
        getTabelaCursosFinalizados().fireTableDataChanged();
        for (String[] curso : finalizados) {
            setRowTabelaCursosFinalizados(curso);
        }
        ec = null;
    }//GEN-LAST:event_bListarCursosFeitosActionPerformed

    public void setRowTabelaCursosFinalizados(Object[] data) {
        getTabelaCursosFinalizados().addRow(data);
    }

    public void setRowTabelaCursosEmAndamento(Object[] data) {
        getTabelaCursosEmAndamento().addRow(data);
    }

    public DefaultTableModel getTabelaCursosFinalizados() {
        return (DefaultTableModel) tabelaCursosFinalizados.getModel();
    }

    public DefaultTableModel getTabelaCursosEmAndamento() {
        return (DefaultTableModel) tabelaCursosEmAndamento.getModel();
    }

