    private void bListarCursosAndamentoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bListarCursosAndamentoActionPerformed
        // TODO add your handling code here:
        EstudanteController ec = new EstudanteController();
        ArrayList<String[]> andamento = ec.getCursosEmAndamento(cpf);
        getTabelaCursosEmAndamento().getDataVector().removeAllElements();
        getTabelaCursosEmAndamento().fireTableDataChanged();
        for (String[] curso : andamento) {
            setRowTabelaCursosEmAndamento(curso);
        }
        ec = null;
    }//GEN-LAST:event_bListarCursosAndamentoActionPerformed

