    public void setDadosEstudante(String nome, String cpf) {
        this.nome = nome;
        this.cpf = cpf;
        dadosEstudante.setText(nome + " (" + cpf + ").");
    }

    public String getNome() {
        return nome;
    }

    public void setNome(String nome) {
        this.nome = nome;
    }

    public String getCpf() {
        return cpf;
    }

    public void setCpf(String cpf) {
        this.cpf = cpf;
    }
